imps = []

for seed in range(1, 101):
    with open(f'100_2nd_layer_{seed}_office_yield', 'rb') as fp:
        yields = pickle.load(fp)
        yields = [-y for y in yields]
        imp = (np.mean(yields) - base_line_default_new[seed - 1]) / base_line_default_new[seed - 1]
        imps.append(imp)

