# print(np.max(imps))
# exit()
term1 = (np.mean(imps) + np.median(imps))/2
term2 = (np.std(imps) + (np.percentile(imps, 75) - np.percentile(imps, 25)) / 2) / 2
print(f'{np.round(term1 * 100, 2)}% +- {np.round(term2 * 100, 2)}%')


